function simpleGridSlider(e){document.addEventListener("click",function(t){t.target.matches(e+" .left")&&(t.preventDefault(),document.querySelector(e+" .simple-grid-slider").scrollLeft-=document.querySelector(e+" .simple-grid-slider").offsetWidth)},!1),document.addEventListener("click",function(t){t.target.matches(e+" .right")&&(t.preventDefault(),document.querySelector(e+" .simple-grid-slider").scrollLeft+=document.querySelector(e+" .simple-grid-slider").offsetWidth)},!1)}
